package CQRS.Generation.util;

/*Generated by MPS */

import java.util.Objects;

public class StringUtil {

  public static String firstToUpper(String input) {
    if (Objects.equals(input, null)) {
      return null;
    }

    if (input.length() == 0) {
      return "";
    }

    if (input.length() == 1) {
      return input.toUpperCase();
    }

    return String.valueOf(input.charAt(0)).toUpperCase() + input.substring(1);
  }


  public static String firstToLower(String input) {
    if (Objects.equals(input, null)) {
      return null;
    }

    if (input.length() == 0) {
      return "";
    }

    if (input.length() == 1) {
      return input.toLowerCase();
    }

    return String.valueOf(input.charAt(0)).toLowerCase() + input.substring(1);
  }

}
